import React, {Component, useRef} from 'react';
import { Link } from 'react-router-dom'
import { uniqueId } from 'lodash'
import { Container, Content } from './styles'
import { Form } from '@unform/web'
import * as Yup from 'yup'

import Logo from '../../assets/logo.svg'

import Upload from '../../components/Upload'
import FileList from '../../components/FileList'
import Input from '../../components/Form/Input'
import Footer from '../../components/Footer'


//inputs de cadastro dos carros

const formRef = useRef(null)

  async function handleSubmit(data, { reset }) {
  try{
    const schema = Yup.object().shape({
      email: Yup.string().email().required('*Digite seu E-EMAIL'),
      nomedaagencia: Yup.string().required('*Digite o nome da agÃªncia'),
      nomedogerente: Yup.string().required('*Digite o nome do gerente'),
      cnpj: Yup.string().required('*Digite seu cnpj'),
      cep: Yup.string().required('*Digite seu cep'),
      bairro: Yup.string().required('*Digite seu bairro'),
      endereco: Yup.string().required('*Digite o nome da rua'),
      cidade: Yup.string().required('*Digite sua cidade'),
      telefone: Yup.string().required('*Digite seu telefone'),
    })
    
    await schema.validate(data, {
      abortEarly:false,
    })

    console.log(data)

    reset()
  } catch (err) {
    if (err instanceof Yup.ValidationError) {
      const errorMessages = {}

      err.inner.forEach(error => {
        errorMessages[error.path] = error.message
      })

      formRef.current.setErrors(errorMessages)
    }
  }
}

class Publicacao extends Component {

  //upload das igm
  state = {
    uploadedFiles: [],
  }

  handleUpload = files => {
    const uploadedFiles = files.map(file => ({
      file,
      id: uniqueId(),
      name: file.name,
      preview: URL.createObjectURL(file),
    }))

    this.setState({
      uploadedFiles: this.state.uploadedFiles.concat(uploadedFiles)
    })

  } 

  render() {
    const { uploadedFiles } = this.state

    return (
      <>
      <Container>

        <Link to="/">
          <img className="logo-login" src={Logo} alt="logo, get car"/>
        </Link>

        <h1 className="bemvindo">Vamos lÃ¡!</h1>

        <Content>
          <Upload onUpload={this.handleUpload} />
          { !!uploadedFiles.length && <FileList files={uploadedFiles}/>}
        </Content>
        
      </Container>
      
      <div className="box-login">
        <h2 className="login-titulo">Login</h2>
        <Form ref={formRef} onSubmit={handleSubmit}>
          <Input className="input-login" name="ID" type="password" />
          <button className="bentrar" type="submit">ENTRAR</button>
        </Form>
        <Link to="/Cadastro" style={{ textDecoration: 'none'}}>
          <p className="criar-conta">CRIAR CONTA</p>
        </Link>
      </div>

      <Footer/>
      </>
    )
  }
}

export default Publicacao;
