import React, { useRef } from 'react'
import { Link } from 'react-router-dom'
import { Form } from '@unform/web'
import * as Yup from 'yup'
import './styles.css'

import Logo from '../../assets/logo.svg'
import Input from '../../components/Form/Input'
import Footer from '../../components/Footer'

function Cadastro() {

  const formRef = useRef(null)

  async function handleSubmit(data, { reset }) {
  try{
    const schema = Yup.object().shape({
      email: Yup.string().required('*Digite seu ID')
    })
    
    await schema.validate(data, {
      abortEarly:false,
    })

    console.log(data)

    reset()
  } catch (err) {
    if (err instanceof Yup.ValidationError) {
      const errorMessages = {}

      err.inner.forEach(error => {
        errorMessages[error.path] = error.message
      })

      formRef.current.setErrors(errorMessages)
    }
  }
  }

  return (
    <>
    <div id="page-login">
      <Link to="/">
        <img className="logo-login" src={Logo} alt="logo, get car"/>
      </Link>

      <h1 className="bemvindo">Vamos lá!</h1>

      <div className="box-cadastro">
        <h2 className="login-titulo">Cadastro</h2>
        <Form ref={formRef} onSubmit={handleSubmit}>
          <Input className="input-cadastro" name="email" type="email" placeholder="E-MAIL"/>

          <Input className="input-cadastro" name="nomedaagencia" type="email" placeholder="NOME DA AGÊNCIA"/>

          <Input className="input-cadastro" name="nomedogerente" type="email" placeholder="NOME DO(A) GERENTE"/>

          <Input className="input-cadastro" name="cnpj" type="email" placeholder="CNPJ"/>

          <Input className="input-cadastro" name="cep" type="email" placeholder="CEP"/>

          <Input className="input-cadastro" name="bairro" type="email" placeholder="BAIRRO"/>

      <div className="en">
          <Input className="input-endereco" name="endereco" type="email" placeholder="RUA"/>

          <Input className="input-n" name="endereco" type="email" placeholder="N°"/>
      </div>

      <div className="en">
          <Input className="input-endereco" name="cidade" type="email" placeholder="CIDADE"/>

          <Input className="input-n" name="endereco" type="email" placeholder="UF"/>
      </div>

          <Input className="input-cadastro" name="telefone" type="email" placeholder="TELEFONE"/>

          <button className="bentrar" type="submit">CADASTRAR</button>
        </Form>
        
      </div>

    </div>
    <Footer/>
    </>
  )
}
export default Cadastro;
