import React, { useRef } from 'react'
import { Link } from 'react-router-dom'
import { Form } from '@unform/web'
import * as Yup from 'yup'
import './styles.css'

import Logo from '../../assets/logo.svg'
import Input from '../../components/Form/Input'
import Footer from '../../components/Footer'

function Cadastro() {

  const formRef = useRef(null)

  async function handleSubmit(data, { reset }) {
  try{
    const schema = Yup.object().shape({
      EMAIL: Yup.string().required('*Digite seu ID')
    })
    
    await schema.validate(data, {
      abortEarly:false,
    })

    console.log(data)

    reset()
  } catch (err) {
    if (err instanceof Yup.ValidationError) {
      const errorMessages = {}

      err.inner.forEach(error => {
        errorMessages[error.path] = error.message
      })

      formRef.current.setErrors(errorMessages)
    }
  }
  }

  return (
    <>
    <div id="page-login">
      <Link to="/">
        <img className="logo-login" src={Logo} alt="logo, get car"/>
      </Link>

      <h1 className="bemvindo">Vamos lรก!</h1>

      <div className="box-cadastro">
        <h2 className="login-titulo">Cadastro</h2>
        <Form ref={formRef} onSubmit={handleSubmit}>
          <Input className="input" name="EMAIL" type="email" placeholder="E-mail"/>
          <button className="bentrar" type="submit">CADASTRAR</button>
        </Form>
        
      </div>

    </div>
    <Footer/>
    </>
  )
}
export default Cadastro;
