import React, { useRef } from 'react'
import { Link } from 'react-router-dom'
import { Form } from '@unform/web'
import * as Yup from 'yup'
import './styles.css'

import Input from '../../components/Form/Input'

function FormPublicacao() {
  const formRef = useRef(null)

  async function handleSubmit(data, { reset }) {
  try{
    const schema = Yup.object().shape({
      ID: Yup.string().required('*Digite seu ID')
    })
    
    await schema.validate(data, {
      abortEarly:false,
    })

    console.log(data)

    reset()
  } catch (err) {
    if (err instanceof Yup.ValidationError) {
      const errorMessages = {}

      err.inner.forEach(error => {
        errorMessages[error.path] = error.message
      })

      formRef.current.setErrors(errorMessages)
    }
  }
  }

  return (
    <>
    <div id="form-publicacao">
      <div className="box-publicacao">
      <h2 className="login-titulo">Cadastro</h2>
        <Form ref={formRef} onSubmit={handleSubmit}>
          <Input className="input-publicacao" name="Nome da agência" />

          <Input className="input-publicacao" name="Marca" />

          <Input className="input-publicacao" name="Modelo" />
          {/* */}
          <Input className="input-publicacao" name="Ano" />

          <Input className="input-publicacao" name="Câmbio " />

          <Input className="input-publicacao" name="Final da placa" />
          {/* */}
          <Input className="input-publicacao" name="Cor" />

          <Input className="input-publicacao" name="Velocidade máxima" />

          <Input className="input-publicacao" name="Tipo de combustivel" />
          {/* */}
          <Input className="input-publicacao" name="Capacidade do tanque de combustível" />

          <Input className="input-publicacao" name="Tração" />

          <Input className="input-publicacao" name="Consumo urbano" />
          {/* */}
          <Input className="input-publicacao" name="Consumo rodoviario" />

          <Input className="input-publicacao" name="KM rodados" />

          <Input className="input-publicacao" name="Potência" />
          {/* */}
          <Input className="input-publicacao" name="Capacidade porta malas" />

          <Input className="input-publicacao" name="Quantidade de lugares" />

          <Input className="input-publicacao" name="IPVA" />
          {/* */}
          <Input className="input-publicacao" name="Pneus são novos?" />

          <Input className="input-publicacao" name="Aceita troca?" />

          <Input className="input-publicacao" name="Preço" />

          
          <button className="bpublicar" type="submit">PUBLICAR</button>
        </Form>
      </div>

    </div>
    </>
  )
}
export default FormPublicacao;
