import styled from 'styled-components'

export const Container = styled.li`
  margin-top: 20px;

  li{
    display:flex;
    justify-content:space-between;
    align-items:center;
    color: #444;

    & + li {
      margin-top:15px;
    }
  }
`;

export const FileInfo = styled.div`
  display:flex;
  align-items:center;

  button {
    border:0;
    background: transparent;
    border:none;
    color: #e57878;
  }
`;

export const Preview = styled.div`
 width: 20px;
`;
